import { useState } from "react";
import { Link } from "react-router-dom";
import Input from "../components/Input";
import './Signup.css';
import Validation from "../components/Validation";
import Error from "../components/Error";

export default function Signup(){
    const [formData, setFormData] = useState({
        name:'',
        mobile:'',
        email:'',
        password:''

    });
    const [errorMessages, setErrorMessages] = useState('');

    const handleChange=(e)=>{
        const {name, value} = e.target;
        setFormData(prevState =>({
            ...prevState,
            [name]:value
        }));
    };
    const errors = {
        name: Validation({ name: 'name', value: formData.name }),
        mobile: Validation({ name: 'mobile', value: formData.mobile }),
        email: Validation({ name: 'email', value: formData.email }),
        password: Validation({ name: 'password', value: formData.password })
    };

    const handleSubmit=(e)=>{
        e.preventDefault();
        const isFormValid = Object.values(formData).every(value => value.trim() !== '');
        setErrorMessages(Object.values(errors).filter(error => error !== ''));
        if(!isFormValid){
           
        }
       else{
        <Error error="Enter all the details required"></Error>
       }

       if (Object.values(errors).every(error => error === '')) {
        localStorage.setItem('signupData', JSON.stringify(formData));
        setFormData({
          name: '',
          mobile: '',
          email: '',
          password: ''
        });
      } else {
        <Error error="Please enter all the details below"></Error>
      }

    }
  
   

    return(
        <div className="container mt-4 d-flex justify-content-center" style={{zIndex:1, position:'relative', paddingBottom:'80px', paddingTop:'80px'}}>
        <form className="form-group p-4 border rounded" style={{maxWidth:'400px'}}>
                <h2 className="text-center mb-4" >SignUp</h2>
                {errorMessages.length > 0 && (
                <div>
                {errorMessages.map((error) => (
                    // <div key={index}>{error}</div>
                    <Error error={error}></Error>
                ))}
                </div>
                )}
                <Input type="text" label="Name" name="name" value={formData.name} onChange={handleChange} requried/>
                <Input type="number" label="Mobile No" name="mobile"  value={formData.number} onChange={handleChange}requried/>
                <Input type="email" label="Email" name="email"  value={formData.email} onChange={handleChange} requried/>
                <Input type="password" label="Password" name="password"  value={formData.password} onChange={handleChange} requried/>
                <button type="submit" className="btn btn-primary btn-block mt-3" onClick={handleSubmit}>Sign up</button>
                <p className="mt-3">Already User? <Link to="/login">Login here</Link></p>
        </form>
           
        </div>
    )
}
