// import { useState } from "react";
// import { useEffect } from "react";
import { useParams } from "react-router-dom";
import './ProductDetail.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faStar} from '@fortawesome/free-solid-svg-icons';

export default function ProductDetail() {
    const { product } = useParams();
    // const [productDetail, setProductDetail] = useState(null);

    // useEffect(() => {
    //     const fetchDetailedProduct = async () => {
    //         try {
    //             const response = await fetch(
    //                 `http://localhost:3001/getproduct/${productId}`,
    //                 {
    //                     method: "GET",
    //                     headers: {
    //                         "Content-Type": "application/json",
    //                     },
    //                 }
    //             );

    //             if (response.ok) {
    //                 const data = await response.json();
    //                 setProductDetail(data);
    //             } else {
    //                 console.log("Failed to fetch product details");
    //             }
    //         } catch (error) {
    //             console.log("Error fetching product details", error);
    //         }
    //     };
    //     fetchDetailedProduct();
    // }, [productId]);

    return (
        <div className="container mt-4"
        style={{
          zIndex: 1,
          position: "relative",
          paddingBottom: "80px",
          paddingTop: "80px",
        }}>
            {product ? (
                <div className="product-conatiner">
                    <img
                        src={product.images[0]}
                        alt={product.title}
                        className="product-image"
                    />
                    <h2 className="product-title">{product.title}</h2>
                    <div className="product-details">
                        <p className="product-detail">Quantity: {product.stock}</p>
                        <p className="product-detail">Category: {product.category}</p>
                    </div>
                    <p className="product-price">Price: Rs. {product.price}</p>
                    <p className="offer-price">Offer Price: Rs. {product.offerPrice}</p>
                    <div className="rating"><p>Rating:</p>
                        <div>
                        {[...Array(product.rating)].map((_, index) => (             
                        <FontAwesomeIcon icon={faStar} key={index} className="star"/>  
                        ))}
                        </div>
                        </div>
                     <button className="add-to-cart-btn">Add to Cart</button>
                    </div>
                    ) : (
                        <p>Loading....</p>
                    )}
                    </div>
                    );
}
