import { useEffect } from "react";
import { useState } from "react";
import AddProductModal from "../components/AddProductModal";
import Products from "../components/Products";

export default function Admin({ user }) {
  const [showAddProductModal, setShowAddProductModal] = useState(false);
  const [products, setProducts] = useState([]);
  const [editProductData, setEditProductData] = useState(null);

  const handleButtonClick = async (action, productId)=>{
    if (action === "edit") {
      try {
        const response = await fetch(`http://localhost:3001/product/${productId}`);
        if (response.ok) {
          const productdata = await response.json();
          setEditProductData(productdata);
          setShowAddProductModal(true);
           
          console.log("Fetched product details:", productdata);
        }
        else {
          console.log("Error fetching product details");
        }
      }
      catch (error) {
      console.log("Error fetching product details:", error);
    }
  }
}


const fetchData = async () => {
  try {
    const response = await fetch("http://localhost:3001/products", {
      method: "GET",
      headers: {
        "Content-Type": 'application/json',
      },
    });
    if (response.ok) {
      const data = await response.json();
      setProducts(data);
      console.log("Displayed products");

    } else {
      console.log("Error fetching products");
    }
  } catch (error) {
    console.log("error", error);
  }
};


useEffect(() => {
  fetchData();
}, []);

const onSubmit = async (formData) => {
  console.log(JSON.stringify(formData));
  try {
    const response = await fetch("http://localhost:3001/add", {
      method: "POST",
      headers: {
        "Content-Type": 'application/json',
      },
      body: JSON.stringify(formData),
    });
    if (response.ok) {
      console.log("Updated products");
      setShowAddProductModal(false);
      Products.fetchData();
    } else {
      console.log("did not update");
    }
  } catch (error) {
    console.log("error", error);
  }
}

return (
  <div
    className="container mt-4"
    style={{
      zIndex: 1,
      position: "relative",
      paddingBottom: "80px",
      paddingTop: "80px",
    }}
  >
    <div className="d-flex mt-3 justify-content-between align-items-center">
      <h4>Admin Dashboard</h4>
      <div>
        <button className="btn btn-block button-style" onClick={() => setShowAddProductModal(true)}>Add Product</button>
      </div>
    </div>


    <Products user={user} products={products} fetchData={fetchData} />
    <AddProductModal show={showAddProductModal} onClose={() => setShowAddProductModal(false)} onSubmit={onSubmit} editProductData={editProductData} fetchData={fetchData} />
  </div>
);
};
