import './Input.css';
export default function Input({type, label, name, value, onChange, required, errorMessage}){
    return(
        <div className="mb-3">
            <label htmlFor={name} className="form-label">{label}</label>
            <input type={type}  className="form-control input-field" id={name} name={name} value={value} onChange={onChange} required={required}/>
            {errorMessage && <Error error={errorMessage}></Error>}
        </div>

    )
}
